/*Data for the table `tbl_article` */

insert  into `tbl_article`(`id`,`create_date`,`delete_flag`,`modify_date`,`author`,`content`,`digest`,`hit`,`is_publication`,`is_top`,`page_key`,`page_title`,`picture_url`,`pub_date`,`tags`,`title`,`article_category_id`) values (1,'2017-06-26 15:50:02','F','2017-06-27 20:38:17','admin@qq.com','<p class=\"MsoNormal\" align=\"center\" style=\"text-align:center;\">\r\n	<b>科技护航 筑牢教育安全防线</b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ——众云教育助力省厅教育安全工作会议历城双语实验学校观摩会成功举办<span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp; 3月<span>23</span>日至<span>24</span>日，全省中小学、幼儿园安全工作研讨会在济南召开，省教育厅总督学关延平，省教育厅学校安全管理处处长魏治法，以及山东省十七地市教育局安全负责人一同参加了会议，共同交流，总结<span>2016</span>、部署<span>2017</span>年学校安全工作。<span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp; 3月<span>24</span>日上午，参会各领导前往历城双语实验学校现场观摩校园安全情况。观摩会由济南市教育局主任督学宋豫主持，会议中，历城双语实验学校胡书记与西安交通大学安全专家王军就教育安全体系的建造与实践等问题进行了分享，“众云教育”提供全程技术支持。<span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<img src=\"/upload/image/20170626/20170626154705_627.jpg\" alt=\"\" width=\"800\" height=\"529\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<b>大势所趋，教育安全平台建设迫在眉睫<span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:22.0pt;\">\r\n	无论是<span>2016</span>年<span>12</span>月颁布的《中小学（幼儿园）安全工作专项督导暂行办法》，亦或是今年教育部长陈宝生关于做好学校安全工作的“一二三四”工作指示，以及“两会”上人大代表提出有必要制定的校园安全法，都是在为学生的校园安全保障提供法律指引与防护保障；云计算、大数据、物联网、移动互联等科技的持续创新更为提升教育安全奠定了强有力的技术基础，而基于两者的教育安全实践，就直接落实到了教育安全平台体系的建设，是“刚需”亦是“大势所趋”。<span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:22pt;\">\r\n	<img src=\"/upload/image/20170626/20170626154817_863.jpg\" alt=\"\" width=\"800\" height=\"529\" title=\"\" align=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<img src=\"/upload/image/20170626/20170626154843_926.jpg\" alt=\"\" width=\"800\" height=\"587\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<b>众云教育助力安全会议，打造“四位一体”安全防护体系<span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;&nbsp;&nbsp; 大数据时代带来的变革，使得平台成为数据的支撑者和载体，更能提供一种数据分析能力，并利用分析结果为我们服务，从而应用于实践。<span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:22.0pt;\">\r\n	众云教育运用“互联网<span>+</span>”等技术手段，聚力打造山东省“学生安全智慧服务平台”，实现教育主管单位、公安机关等政府部门、学校及家长之间的互联互通，实现预警、实时监控、轨迹追踪，达到“可预防、可排查、可教育、可联动”的整体目标，构建教育局、学校、公安、家长共同参与的教育安全防护体系，“四位一体”以实现安防工作由被动防御向主动防御的过渡。<span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:22pt;\">\r\n	<img src=\"/upload/image/20170626/20170626154932_151.jpg\" alt=\"\" width=\"800\" height=\"529\" title=\"\" align=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:22.0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	非止排难于变切，亦将防患于未然。此次观摩会不仅使各参会领导直观了解了学校对安全平台的使用情况，更增强了进行校园安全建设的信心，得到了省市教育局负责人的充分认可与肯定，大家对部署四级平台达成共识，并呼吁加快全省一体化平台的建立。\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"line-height:1.5;\">习近平总书记强调，孩子们成长得更好，是我们最大的心愿，而保障孩子安全恰是众云教育不变的初心。以科技助力，众云教育将教育与保护的责任落到实处，筑牢校园安全的防护墙，为教育事业发展保驾护航！</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>','',19,1,0,'','','','2017-06-26',NULL,'科技护航 筑牢教育安全防线',2),(2,'2017-06-26 16:14:44','F','2017-06-27 11:31:44','admin@qq.com','<p class=\"MsoNormal\" style=\"text-align:right;\" align=\"right\">\r\n	<b>国教督办〔<span>2017</span>〕<span>5</span>号<span></span></b> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\">各省、自治区、直辖市教育厅（教委），新疆生产建设兵团教育局：</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\">近期，广西、河南、四川、山东等地中小学、幼儿园（以下简称学校）发生安全事故，造成学生伤亡。这些安全事故的发生，反映出一些地方和学校存在安全意识不强、制度落实不到位等问题。为切实加强学校安全管理工作，保障学生安全，现紧急通知如下：</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:14px;\">一、切实提高认识。</span></strong><span style=\"font-size:14px;\">各地教育行政部门和学校要以对党和人民高度负责的态度，充分认识做好学校安全工作的重要性、紧迫性，牢固树立安全工作红线意识和底线思维，坚持以人为本、安全第一、预防为主、综合治理，不断研究学校安全工作中出现的新情况、新问题，加强工作的前瞻性、预见性，狠抓落实，切实保障广大师生生命财产安全，为党的十九大胜利召开营造良好氛围和环境。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:14px;\">二、完善工作机制。</span></strong><span style=\"font-size:14px;\">各地教育行政部门和学校要进一步完善校园安全风险管控机制，按照学校岗位安全工作职责要求，严格落实各项管理制度和措施，将安全管理融入到学校日常工作全过程。要完善校园周边治安综合治理机制，加强与公安、综治、宣传等有关部门在信息沟通、专项整治、应急处置、舆情应对等方面的协调配合，形成常态化的安全监管工作机制。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:14px;\">三、开展隐患排查。</span></strong><span style=\"font-size:14px;\">各地教育行政部门和学校要以防溺水、校舍、消防、食品、上下学交通、学生欺凌与暴力等为重点，结合本地实际，立即组织开展一次安全隐患大排查。对排查出的问题，要按照“谁主管、谁负责”的原则，建立台账、落实责任、细化措施，明确时限，确保全面整改落实。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:14px;\">四、严格信息报送。</span></strong><span style=\"font-size:14px;\">各地教育行政部门和学校要认真落实教育系统突发公共事件应急处置工作有关要求，严格执行信息报送制度，遇有突发事件和重要紧急情况必须第一时间上报教育部总值班室。对不第一时间报送或瞒报、漏报安全事故信息的，要严格按照相关规定严肃追究当事人和相关领导的责任。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:14px;\">五、强化应急管理。</span></strong><span style=\"font-size:14px;\">各地教育行政部门和学校要建立健全学校安全事件应急管理机制，完善、细化各项应急处置预案，落实人员职责，做好应急工作准备，确保一旦发生突发事件，能够快速反应、高效应对、迅速管控。要严格按照“属地管理”和“分级管理”的原则，根据突发事件分级标准，科学决策、精心组织、统一指挥、妥善处置，最大限度降低学校安全事故危害。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:14px;\">六、做好舆情应对。</span></strong><span style=\"font-size:14px;\">各地教育行政部门要在当地党委、政府的领导下，加强与宣传、公安等部门沟通协作，建立健全教育舆情实时监测机制，及时跟踪、分析教育舆情，发布预警信息，努力掌握教育舆情的主动权。要加强与媒体的沟通，第一时间发布准确信息，引导舆论理性表达，适时公布事故处置情况，及时回应社会关切，正确引导社会舆论。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:14px;\"> 七、加强安全教育。</span></strong><span style=\"font-size:14px;\">各地教育行政部门和学校要根据学生身心特点和认知能力，把安全教育贯穿于学校教育各个环节，灵活采取多种形式和载体，着力提高针对性与实效性。要定期开展各类专题教育，经常性地开展应急疏散演练，不断提高学生的安全防范意识、自救自护技能和应急处置能力。要积极做好学生家长的安全教育宣传工作，严防脱离学校监管期间发生安全事故。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:14px;\">八、加强督导检查。</span></strong><span style=\"font-size:14px;\">各地教育督导部门要按照《中小学（幼儿园）安全工作专项督导暂行办法》要求，将学校安全工作列为教育督导的重要内容，加强对地方政府及各有关部门、学校落实安全工作职责的督导检查，将督导结果作为评价政府教育工作和学校管理工作成效的重要内容。</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:right;\">\r\n	<span style=\"font-size:14px;\">国务院教育督导委员会办公室</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:right;\">\r\n	<span style=\"font-size:14px;\">2017</span><span style=\"font-size:14px;\">年</span><span style=\"font-size:14px;\">4</span><span style=\"font-size:14px;\">月</span><span style=\"font-size:14px;\">19</span><span style=\"font-size:14px;\">日</span><span></span> \r\n</p>','',3,1,0,'','','','2017-06-27',NULL,'国务院教育督导委员会办公室 关于加强中小学（幼儿园）安全工作的紧急通知',1),(3,'2017-06-27 09:06:52','F','2017-06-27 11:32:08','admin@qq.com','<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强中小学校安全教育和管理工作，牢固树立“安全第一”思想是根本，明确并强化职责是核心，健全并落实制度是保证，狠抓措施落实是关键。各学校要对照本指南结合实际明确本校各个岗位的安全职责，健全学校安全管理制度，落实各项制度措施，切实将安全教育和管理融入到学校日常工作的各个环节，确保广大师生安全，确保校园和谐稳定。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>一、学校安全工作领导小组</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">全面负责学校安全工作，校长是领导小组组长，其他成员分工负责。领导小组下设安全保卫机构（保卫处），保卫主任由分管副校长分管。配备一定数量的专（兼）职保卫人员，建立高效规范的学校安全工作网络体系。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">学校安全工作领导小组下设应急小组：指挥组、保卫组、现场处置组、现场救护组、通讯联络组、后勤保障组、事故调查组等。各组根据事故实际情况，启动工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">切实保证学校安全工作所需人、财、物并合理配置。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制定学校各项安全管理制度、预警和突发事件应急预案，完善事故防范措施，检查督导安全工作“一岗双责”制度的落实。协助有关部门对重大安全事故做出处理，并在适当范围内通报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期召开领导小组专题会议，组织学习上级部门下发的安全工作指导文件，制定年度学校安全工作计划，拟定安全目标管理责任书。结合学校特点研究部署学校常规性安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">代表学校与家长签订安全协议书。由校车服务提供者提供校车服务的，学校应当与校车服务提供者签订校车安全管理责任书。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">强化人防、物防、技防手段，抓好校舍设备维护、消防、治安、交通、食品、疾病预防、自然灾害防范等基础性安全工作。定期开展自查，及时排除安全隐患。重点做好校门秩序、教育教学、学生宿舍、食堂卫生、大型集体活动、集体外出等方面的安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">组织开展师生安全宣传教育和培训，定期不定期开展应急演练，提高师生对各类突发事件应急处置能力和逃生自救技能。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在上级部门的指导下和学校周边单位建立校园周边综合治理小组，或建立联席会议制度。注重学校安全长效机制建设；加大校园周边综合整治力度，维护校园及周边安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">发生紧急情况立即启动应急预案，全面负责突发事件的指挥、协调等工作，及时组织抢险抢救。在有关部门领导下及时、妥善、依法处置事故。对相关人员进行责任追究。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格履行事故报告制度，及时向上级有关部门报告情况，做到</span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">30</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">分钟内电话口头报告，</span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">小时内简要书面报告。密切配合医疗、防疫、公安、消防等部门对事故的处理，认真执行上级有关指示。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">12.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">可设对外新闻发言人负责接待各界媒体，遇到突发事件时能冷静面对媒体采访，形成正确的舆论导向。教育师生员工共同做好稳定工作，未经同意不得随意接受采访、擅自发布信息。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">13.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强对教师侮辱、体罚学生现象的监管。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二、校长</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">学校的法定代表人，学校安全工作的第一责任人。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">认真贯彻落实国家有关学校安全工作的法律法规和上级对学校安全工作的部署。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">全面负责学校安全工作，建立健全组织机构和防范体系，落实责任制，依法制定学校各项安全管理制度和应急预案。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">建立安全工作奖惩制度，把安全工作纳入各部门、个人履职考核，与评优推先和绩效考核挂钩，调动全体教职工共同做好学校安全工作的积极性。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">组织召开学校安全工作领导小组会议，分析研究学校安全工作现状及存在的问题，有针对性地制定学校安全工作计划。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">及时制止和处理教职工侵犯学生权益和影响学生身心健康的行为。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强与所属乡镇、街道、社区、派出所、消防、卫生、城管等部门的联系，取得他们的支持和配合，共同做好校园及周边安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">遇到突发事件立即组织安全领导小组启动应急预案，并第一时间赶到现场指挥。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">学校安全职责所必需的其它行为。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三、党</strong></span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\"><strong>(</strong></span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>总支、支部</strong></span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\"><strong>)</strong></span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>书记</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">校园安全的共同责任人，对学校安全工作负主要领导责任。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助校长管好学校安全工作，校长不在校期间，履行校长岗位安全职责。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">通过党建工作加强安全教育，提高党员干部的安全责任意识，指导落实岗位安全职责。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">关心教职工的生活和思想动态，及时掌握校内不稳定因素，积极化解矛盾，维护校园安全稳定。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">督查学校安全管理制度落实，向学校安全工作领导小组提出整改建议。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">积极组织开展反邪教工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>四、分管安全工作的副校长</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在校长的领导下，具体负责学校安全工作，对学校安全工作负直接领导责任。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">可代校长组织召开学校安全工作领导小组会议，传达学习上级有关安全工作的文件，研究学校安全工作存在的问题和隐患，提出解决问题的方法和整改意见，确保学校安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">根据上级要求，依据相关法律法规，不断完善、建立健全学校安全工作管理制度，组织制定各种突发事件应急预案。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">全面落实学校安全工作责任制，层层签订学校安全工作责任书，把学校安全工作任务分解到各处室、部门和岗位，并负责检查、督导落实。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">指导学校安全专职干部和各部门负责人开展工作，定期不定期检查各处室、部门、岗位的安全防范和隐患排查工作，并建立相关台账，在相关记录表上签字。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期组织培训学校安全保卫干部和员工，强化安全责任意识，提高防范和应对突发事件的能力，杜绝因思想麻痹或工作失误造成的安全责任事故。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强师生安全教育和培训工作，定期组织开展各类宣传和应急演练活动，提高师生安全意识和自护能力。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期组织检查学校各部门的安全设施及器材，保证完好有效。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助校长加强与所属街道、社区、派出所、消防、卫生、城管等部门的联系，积极开展校园及周边综合治理工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">建立健全学校安全管理各类台账和档案制度，检查指导学校各类安全资料的归档备案。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">督促检查放学前一分钟、每周一节课、每月一次专题讲座的安全教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>五、分管教学副校长</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">分管教学的副校长是学校教学工作的安全责任人，主要职责有：</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">督促全校教师严格落实教学常规。特别是认真落实体育课、劳动课、实验（训）课等教学常规，防止学生意外伤害事故的发生；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">对必须在校外进行的教学活动要认真审批，要有严密的安全措施，确保活动安全进行；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">督促全校教师加强师德修养，关爱学生，不得体罚和变相体罚学生，切实减轻学生过重课业负担。要采取切实可行的措施，杜绝因体罚和心灵虐待造成对学生的伤害事件，确保学生身心健康发展；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">督促指导教师把学生安全作为教育教学的第一要事，摆在首位，认真落实各有关法规和上级文件对教育教学工作的安全规定，依法办事，依法执教；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成上级交办的其它安全工作任务。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>六、分管后勤副校长</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">分管后勤的副校长是学校设施设备安全责任人，其主要职责有：</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制定学校设施设备安全管理工作规章制度，做好学校设备设施安全管理工作；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">组织对学校建筑物、设施设备的安全检查，特别是消防安全检查，及时消除安全隐患，杜绝安全事故；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强对食堂、超市、自备水源的管理，确保学校食品及饮用水安全；加强学校卫生防疫工作；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强学校宿舍设施安全管理，确保师生宿舍安全；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">督促有关人员做好防盗工作，维护学校财产安全；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强学校的安全设施建设，确保校园安全；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成上级交办的其它安全工作任务。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>七、法制副校长</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助学校按照有关法律法规制定完善校园安全管理制度，落实各项安全防范措施。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助学校加强安全工作，结合中小学学生特点，开展有针对性的交通、消防、治安等安全宣传及法制教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助学校做好对有不良行为学生的教育转化工作，落实具体帮教措施。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协调有关部门对学校周边治安环境进行整治，严肃查处侵害师生合法权益和滋扰校园案件，建立长效机制，维护学校周边治安秩序。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">配合政法部门妥善处理在校师生违法案件，督促学校妥善处理校园内发生的严重违规违纪问题。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助学校与社会、家庭等方面建立联系，完善“三位一体”法制教育机制，落实各项治理措施。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>八、工会主席</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">发挥工会组织对校园安全工作的参与和监督作用。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期召开工会委员会会议，检查各部门安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助学校摸查内部人员的不稳定因素，及时化解矛盾。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">关心教职工的思想动态和文化生活，帮助教职工解决生活中的困难和问题。对发生重大事情的家庭应及时慰问并家访。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>九、保卫主任</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在校长和分管副校长的领导下，具体负责学校日常安全管理工作。根据学校安全工作计划，制定部门实施细则，定期向分管安全工作副校长汇报学校安全工作情况。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">按时参加上级有关部门召开的安全会议并按相关要求，上报学校安全工作计划、总结、报表、材料、信息等。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">坚持每天对校园及重点部位进行巡查，发现安全隐患，立即责成有关部门进行整改并启动责任追究制度，规定完成整改的时间。问题严重的要及时向分管副校长汇报，制定详尽的整改方案。建立健全安全隐患排查整改台账，相关负责人及时在记录表上签字。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">结合学校安全工作实际，开展治安、消防、交通等校园安全宣传教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">履行校园日常安全管理和活动安全管理职责，检查督促各部门落实各种活动安全预案及安全措施。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责校园“三防”（人防、物防、技防）建设，管理安保人员，维护安防设施。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责学校门卫管理，夜间、节假日值班和巡逻安排，加强对值班人员的管理和检查。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责全校消防栓、灭火器、报警器、疏散通道等消防设备的日常检查和维护，保证校内消防器材的完好、有效，确保正常使用。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">配合学校安全工作领导小组主动与相关部门联系协调，建立密切的工作关系，搞好校园周边环境综合治理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">校内发生安全事故或突发事件，要在第一时间赶到现场，及时向学校安全工作领导小组汇报，并根据应急预案配合指挥组落实报警、抢救、疏散、保护现场、调查取证、信息上报等工作，妥善处理突发事件。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">根据有关规定妥善保管视频监控录像资料，并建立资料档案。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">12.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十、办公室主任</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">会同有关处室负责学校安全宣传工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责本部门安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责学校安全教育和演练活动的讲话、录像等资料收集整理工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责全校信息沟通、信息公开、新闻发布及媒体采访等事宜。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十一、德育主任</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">结合学校安全工作目标和任务，开展学生德育工作，维护校园教育教学秩序、稳定师生情绪。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">组织落实学校安全工作领导小组安排部署的工作任务，依据部门安全岗位职责，对年级组长和班主任开展针对性的安全宣传教育和培训，指导年级和班级开展丰富多彩的安全教育和演练活动。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期召开年级主任和班主任安全工作会议，组织年级主任和班主任进行校园安全隐患自查互查活动，跟踪每节课学生异动情况，增强年级主任和班主任的安全意识，落实安全责任。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在学生会和班委会设立安全委员，设“学生</span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">110</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">”和班级安全员，协助做好学生安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">学校组织大型集体活动时，做好教育活动安全预案和活动前师生安全教育工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">利用升旗、广播操等时间，开展学生安全教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">通过板报、橱窗、广播、网络等开展对师生安全宣传教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">聘请消防、治安、交通、卫生等专家开展对师生的专题安全教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">通过家长会、家长信、短信等多种形式向家长宣传安全知识，提出安全要求，发挥家长对学生安全工作的主体作用。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好安全教育、家长接待、事故处理及相关材料的整理归档。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责住校生的教育和管理工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">12.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责卫生、防疫安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">13.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十二、教务主任</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责教务处所属各组（室）的安全管理工作，落实部门安全责任和各项安全管理制度，制定部门安全工作计划，并监督、检查、工作落实情况。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期召开部门安全工作会议。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好各种教具配备、保存及使用的安全管理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强学生学籍信息的安全管理，建立信息保密制度。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好教学活动和考试安全预案及管理工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">配合校长积极稳妥做好学校招生、毕业工作，正确执行政策、有效化解矛盾，维护校园安全稳定。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十三、总务主任</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责学校总务后勤的安全管理工作，落实部门安全责任和各项安全管理制度，做好部门安全工作计划，监督、检查工作落实情况。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期召开部门安全工作会议。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强总务后勤人员的安全培训，提高各工种人员的安全意识和防范技能。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好学校建筑物和水、电、气等设施设备的安全检查和维护，根据保卫主任的要求定期维护或更换消防器材和特种设备，并建立台账，及时做好相关记录。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">切实加强学校食堂及食品卫生管理，严格执行《中华人民共和国食品卫生法》、卫生部《学生集体用餐监督管理办法》等法律法规，建立严格的学校卫生管理制度，自觉接受卫生防疫部门监督。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">组织相关人员按规定定期进行安全培训和体检；建立食品留样制度；随时掌握当地流行性疾病、食源性疾病和饮水污染的情况，采取切实有效的预防措施，杜绝疾病、流行性疾病和食品中毒事故的发生。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">落实本部门不同工种安全责任。组织部门人员定期检查负责范围内的安全”。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十四、少先队辅导员、团委（支部）书记</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">贯彻执行安全法律法规、学校安全管理规定，履行部门岗位安全职责。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在少先队和共青团组织中设立安全委员。经常开展对少先队员和共青团员的安全教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制定少先队、共青团组织的各类大型集体活动预案，开展活动前的安全教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期组织学生活动场所的安全检查，发现隐患及时整改，并向学校有关部门汇报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">熟悉学校各类安全预案及流程，协助和配合学校有关部门处置突发事件。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十五、教科（研）室主任</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">指导学科教师结合所教学科内容渗透安全教育，引导每个教师要因地制宜、因势利导开展学生安全教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强课堂、特别是实验课的安全管理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">督促每位任课教师安全教育教案和开展“放学前一分钟安全教育”。每天最后一节课下课前，结合实际提醒学生注意交通安全、防劫防骗、防各种伤害事故等安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十六、教研组长</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">教研组安全第一负责人，负责建立教研组日常安全管理制度，落实学校各项安全管理要求。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期召开教研组安全工作会议，维护学校教育教学安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制定实验室安全规章制度，督促实验员做好实验室的安全管理工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制定实验室突发事件应急预案并组织演练。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">监督指导各学科教师在学科教学中渗透安全教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十七、年级组长</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">所在年级的安全第一负责人，负责建立年级安全工作的各项规章制度，明确本年级每位教师的安全岗位职责。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">建立健全由年级组长、班主任、任课教师、学生构成的年级安全管理体系，定期排查年级存在的安全隐患，促进年级安全工作常态化、制度化。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">及时传达上级有关安全工作文件精神，组织年级教师定期召开安全工作会议，落实学校安全工作有关要求，做好日常安全防范。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:18pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好年级特异体质学生身体情况登记和安全管理工作。制定年级大型活动安全预案并做好活动前的安全教育工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">指导班主任和任课教师加强对学生的安全教育和管理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责本年级学生楼层、楼梯的安全管理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十八、班主任</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">班级安全管理的第一责任人，对本班学生安全及教室内的设施设备安全负责。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">认真落实学校安全工作的各项要求，及时解决班级出现的安全问题，排除安全隐患。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在班委会和团支部设立安全委员，在班级设立若干安全员。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">保证晨（午、晚）检专时专用，仔细查看学生精神和身体状态，认真记录，及时上报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">充分利用晨（午、晚）检、班会等时间开展学生安全教育。特别注意根据季节变化提醒学生预防疾病，防范各种可能发生的自然灾害和安全事故，提高学生安全防范意识和逃生自救技能。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格执行学生考勤和请销假制度，做好学生考勤统计工作，及时了解未到校上课或中途离校学生的情况，并及时与家长取得联系，做好记录。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">认真做好班级内各种不安全因素的排查和登记工作，发现问题及时向年级组长和校领导汇报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">对有特异体质和心理异常的学生，应在家长的配合下及时做好记录。在安排体育、劳动、大型活动等时予以照顾。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助学校与家长签订安全协议书，并做好协议书回执留存。通过家长会、家访等形式开展家长安全教育，让家长切实担负起监护人职责，做好学生安全教育监管工作，特别是保障学生校外安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">组织班级集体活动必须征得学校领导同意并报上级教育行政部门批准后方可进行，做好安全预案和活动前的安全教育工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">发现学生在校出现身体不适或危险情况时，要立即采取措施、组织抢救，并及时通知家长、报告学校。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">12.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">开展“放学前一分钟安全教育”。结合实际提醒学生注意交通安全、防劫防骗、防各种伤害事故等安全事项。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">13.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">认真准确采集学生及家庭相关信息。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">14.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>十九、任课教师</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">明确并履行岗位安全职责，落实学校安全工作的有关要求，做好安全防范工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">将安全教育有机渗透到本学科教学内容和教学过程中。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">课前清点学生人数并立即上报班主任，课堂上发现学生行为具有危险性时应及时制止、告诫、教育，并与班主任或学生家长及时沟通。课间负责本楼层就近楼梯间、楼层的安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">密切配合班主任开展安全工作，及时将班内的安全问题向班主任反映，协助班主任对学生进行安全教育，妥善处理班级出现的安全问题。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">课堂教学中如遇突发事件或安全问题，及时将学生有序疏散到安全地带并作妥善处理，同时立即向分管领导或校长汇报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">开展“放学前一分钟安全教育”。每天最后一节课下课前，结合实际提醒学生注意交通安全、防劫防骗、防各种伤害事故等安全事项。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十、体育教师</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">掌握教学班学生特异体质状况，在体育教学中合理安排相关学生活动，并做好记录。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好课前准备，根据教学内容，对场地、器材及教学环境进行全面安全检查，及时排除安全隐患。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">结合体育项目特点，讲解安全注意事项，带领学生认真做好准备活动，强化学生相互保护意识，提高学生自我保护能力。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">遵循体育运动规律，严格按照体育教学要求上好每节课，不违规操作。特别是体操教学，要做好“保护与帮助”的示范动作，教会学生如何进行“保护与帮助”。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格教学过程管理，把控学生动态。认真、及时解决好学生在体育教学过程中的各种矛盾或纠纷，</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">掌握体育教学及体育课外活动中发生伤害事故的处理方法及程序，严格按照学校“学生体育教学及体育课外活动伤害事故（急病）应急预案”程序进行处理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十一、电教教师</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责学校电教设备维护与安全运行。及时检查和报告设备隐患，配合电工检修，保障电教设备安全运行。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">认真执行网络有关安全规定，确保校园网络安全。杜绝不良信息在校园网页上出现。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好防火、防盗、防潮工作。掌握灭火常识，会使用消防器材，紧急时刻具有扑救初起火灾的能力。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">一旦发生意外事故，积极采取救护措施，及时报告有关部门并保护好现场。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">每天下班时检查水电门窗，关闭所有应关电源，确认无安全隐患后方可下班。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十二、心理教师或心理健康学科任课教师</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好学生心理辅导工作，普及心理健康和青春期知识。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">建立心理健康咨询室，负责做好心理健康咨询室及心理健康教育有关设施设备的安全维护。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好全校学生心理调查工作。配合班主任、家长对有心理问题的学生进行心理疏导，并做好跟踪随访和相关记录，及时汇报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十三、财务人员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">提高安全防范意识，增强责任心，确保学校财务安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强印章、空白支票的保管和使用安全。空白支票与财务用章分开存放，严格按照财务规定存放现金。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">到银行取送款时，大宗款项必须三人以上专车接送，少量款项须两人以上接送。使用安全包，取送款人员必须做到人不离款、款不离身，确保现金和人身安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">确保学校财务账目、票据保存规范，避免因存放不当受潮损坏。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好财务室防火、防盗工作，做到人离门锁。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">下班时关好水电门窗，确认无安全隐患后方可下班。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十四、财产管理员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">全面负责、统一管理学校财产，建立台账确保所保管物品的安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">管理好学校仓库，建立仓库和财产管理制度。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强仓库消防安全，严禁火种入库，严禁库内吸烟和使用各种电热器具，定期检查更换库房的消防器材。对存放物品要留出顶、灯、墙、柱、堆等防火间距。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">仓库物品必须分类、有序、限额存放。做好防火、防盗和防潮工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">一旦发生意外事故，积极采取救护措施，及时报告有关部门并保护好现场。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">每天下班前对仓库进行安全检查，并作记录。下班时关好水电门窗，确认无安全隐患后方可下班。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十五、图书管理员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">学校图书馆安全工作第一责任人，切实落实各项安全管理制度，确保图书馆安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">图书馆开放期间，组织学生有序出入图书馆，做好安全门的标示和开启工作，防止发生拥挤踩踏伤害事故。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">图书馆内不得存放易燃品和私人物品，严禁吸烟。认真做好防火、防盗、防潮、降湿、防蛀等安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强电子阅览室的管理，注意网络安全，保证电子书籍的健康。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">一旦发生意外事故，积极采取救护措施，及时报告有关部门并保护好现场。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">每天对图书室、电子阅览室进行安全检查，并作记录。下班时关好水电门窗，确认无安全隐患后方可下班。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十六、档案管理员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">按上级规定和学校要求妥善保管档案资料，落实保密制度，确保档案安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">按有关要求安装防火防盗门，按规定配齐灭火器材，并及时检查、维护或更换，有条件的学校配备安全报警装置，做好防火、防盗工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">按天气情况开启空调和除湿机，维护档案室的温度和湿度，做好档案的防潮、降湿、防蛀工作，确保档案资料的安全与完整。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格执行档案查阅制度。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十七、体育器材保管员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">坚持每天检查体育运动器械、户外运动器材和场地，并做记录。发现安全问题立即停用，并将问题器械做封存警示，及时向有关领导（部门）报告并组织维修和处理，消除安全隐患。杜绝使用存在安全隐患的器材或器械。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">教育学生爱护器材，指导学生安全使用器材，教育学生发现器材安全问题及时向老师报告。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">坚持体育器材领借制度，器材室所借出的器材须符合安全标准，并及时归还、验收。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">借出的运动器材不允许放置在无人看管的场所。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十八、实验室管理员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">实验室安全第一责任人，在学校相关部门及教研组长的指导下，认真制定实验室的安全规章制度，并贯彻落实。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期不定期检查仪器，及时保养、维修，做到实验室内仪器、药品放置整齐，使用安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格执行有关规定，管理好有毒、有害、易燃易爆物品，做好采购、登记、储存、使用和处置工作，及时上报需处置的过期物品，加强实验室的安全管理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">按规定配置灭火器材，及时检查、维护、更换。禁止无关人员进入实验室。做好防火、防盗工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">开展学生安全教育，指导学生安全使用实验用具和药品，防止伤害事故的发生。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制定突发事件应急预案并组织演练，掌握各类事故发生时的处置方法。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">每天严格检查实验室及药品存放安全，并作记录。下班时关好水电门窗，确认无安全隐患后方可下班。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>二十九、食堂管理员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">食堂安全第一责任人，在主管领导的指导下具体负责学校食品安全的全面工作。建立健全相应管理档案。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期组织食堂从业人员参加食品安全培训。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期组织食堂工作人员健康体检，确保食堂所有工作人员持证上岗、安全上岗。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格遵守学校食堂食品采购索证制度、进货验收制度、厨房烹饪制度、卫生消毒制度、食品留样制度，不采购“三无”食品和腐烂变质食品，严把食堂生产各个安全环节。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好食堂防火、防潮、防尘、防虫害各项工作，定期检查维护食堂设备，要重点检查燃气、灶具、油烟管道、锅炉等重要设备，聘请专业人员定期清洗管道烟道，确保各项设施设备安全运行。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">不用的煤气罐不得放在灶具旁，应保管在安全地方。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">每天严格检查厨房、库房、燃气、水电、设备安全，并作记录。下班时关好水电门窗，确认无安全隐患后方可下班。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十、宿舍管理员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">宿舍安全和设施设备安全使用的第一负责人。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">建立住宿学生及其家长档案，建立特异体质学生档案，建立住宿生班级管理和班主任联系制度，管理好学生家长和班主任的联系电话。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在学校德育处（学生处）和保卫处的指导下成立学生宿舍管理委员会，每个宿舍设宿舍长，配合宿舍管理员做好住宿生的各项管理工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好宿舍大门和出入人员管理，按时开关宿舍大门，严禁非住宿人员进入宿舍。认真落实宿舍管理值班制度，值夜班时不得睡觉。宿舍大门朝外开，夜间严禁上锁。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期检查维护宿舍各类设施和用品（门、窗、水、电、床、柜、暖气等），发现安全隐患及时报告相关领导或部门，配合有关部门及时消除隐患。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">加强宿舍区消防安全管理，严禁私拉乱接电线或使用大功率电器，严禁安全通道堆放物品，定期不定期开展宿舍区火灾等应急疏散演练，每间宿舍门后张挂疏散线路图。确保消防通道畅通。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">掌握消防栓、灭火器的正确使用方法和扑救初起火灾的能力，突发事件来临时，组织学生有序疏散并及时上报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格执行学生请销假制度，学生请假回家要填写请假登记表，并告知家长和班主任，取得他们的同意方能准假。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">每晚熄灯前，清点各宿舍人数，发现有学生未归的，要立即查找，并及时与家长和班主任联系。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">每晚熄灯后，认真巡逻宿舍及周边环境并督促学生就寝，同时认真检查门、窗、水、电。巡逻时若发现学生生病，应及时与值班校医联系诊治，严重的要立即送往医院，并告知家长和班主任。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">学生上课后，逐个检查宿舍。发现滞留在宿舍的学生要问明情况，如有生病的学生，要及时采取救治措施，并立即通知家长和班主任。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">12.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好宿舍环境卫生和喷洒消毒管理工作，组织指导宿舍管理委员会定期评比宿舍卫生并公布，对宿舍内务和卫生差的要及时进行教育，并限时整改。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">13.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十一、校医</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">学校卫生安全工作第一负责人，负责学校的卫生保健和疾病预防工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在德育处（学生处）的配合下，成立学校红十字会。每班设立生活委员和红十字委员，协助班主任做好卫生保健工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在德育处（学生处）的配合下，建立各班学生健康档案，为有特异体质的学生单独建档。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责学校卫生安全教育工作，积极开展师生食品卫生、疾病预防及急救常识的培训和教育。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">坚持每日查看各班晨（午、晚）检记录，发现异常，及时通知家长并尽快将患病学生送正规医院诊治。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">负责学生卫生保健工作（组织体检、预防接种、喷洒消毒、疾病防控、近视眼和龋齿防治等），及时处理学生常见病，做好治疗登记。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制定学校突发疾病、食物中毒等应急预案，发生突发事件，立即启动应急预案，及时报告相关领导和部门，做好事故处理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">配合德育处（学生处）组织全校大扫除和卫生检查，做好卫生评比和成绩公布，督促全校师生做好公共卫生工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">配合总务处定期检查食堂各项安全卫生制度落实情况，并建立检查记录台账。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好卫生室常用药品采购工作，保障供给。使用、维护和管理好学校医疗器械并按有关规定做好器械消毒工作，建立消毒登记档案，加强对废弃物品的安全卫生管理。学校医疗器械必须专人使用，一律不得外借。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十二、文印室人员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">文印室内严禁烟火，严禁非工作人员擅自进入。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期清理废弃纸张、油墨等易燃物品，定期检查文印室各类线路，防止因线路老化、超负荷工作发生火灾，定期检查文印室内消防设施，保证灭火器材完好有效。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期进行文印室各类安全隐患排查。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">遵守文件保密制度，严防泄密。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">下班时关好水电门窗，确认无安全隐患后方可下班。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十三、水电工</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">持证上岗，严格按照操作规程作业。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">每天检查配电室、校园照明、网络、电话、广播、供水等设施设备，确保水电安全及时供给和运行。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助宿舍管理人员检查宿舍用电情况，严禁私接线路、安装插座、使用自备大功率电器。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">落实水电安全隐患排查制度。严防电路漏电、短路、电线老化、超负荷，消除水路跑、冒、漏等不安全因素，建立水电安全维护台账，发现隐患及时整改和报告。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十四、门卫</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">执行</span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">24</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">小时值班制度，不得擅自离岗、空岗，按时交接班并做好交接记录</span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">,</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">发现问题及时报告。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格落实校门开闭制度，学生上课时间应保持校门关闭。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严格执行大门和出入人员管理制度，来访人员需进行严格验证，并依据学校有关会客登记制度履行登记手续。严禁无关人员进入校园。上课期间，学生必须持有班主任开具的出门证，方可出校。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">发现可疑人、事、物或其他治安信息，及时向相关领导或部门汇报。必要时，启动报警器或向</span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">110</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">报警，并配合公安机关做好处置工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">对出入人员和车辆所携带、装运的物品、物资进行严格的检验、核查，禁止私自将危险或违禁物品带入。严防学校物资流失。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">疏导出入车辆和行人，清理门卫责任区内的无关人员，保证进出车辆畅通，人员出入有序无阻。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">任何人不得在警卫室从事与该室工作无关的活动，不得存放或代人存放贵重物品、现金和危险品。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好警务室的消防安全工作，确保灭火器材的充足、完好和有效，定期检查插头、电线，发现问题及时报修。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">妥善保管、定期检查、熟练使用技防工具，在危急时刻充分发挥技防工具的作用，确保师生安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在岗期间不与无关人员聊天，不干私活，不饮酒。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十五、保卫人员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在保卫主任的领导下开展工作，落实相关部门及学校保卫工作规章制度和要求，按时参加上级部门的保卫工作会议，并及时向领导汇报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">熟悉业务，提高防范破案能力，记录并协助有关部门处理学校发生的案件，确保师生安全。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好节假日和平时的值班，及时解决值班中出现的问题。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">积极防范，组织和参加校内巡逻，发现安全隐患及时整改或向相关领导报告，提出整改意见。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">检查指导警卫室、重点、要害部门人员的安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">保持与班主任、任课教师的联系，加强对个别不遵纪守法学生的法制安全教育，协助做好违法青少年的帮教工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">保护学校财产和师生人身安全不受侵害，发现问题及时处理。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十六、学校保安</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">熟悉值勤岗位区域内的地形、地物，了解值勤岗位区域内的消防设施分布和使用，掌握报警方法。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">掌握保安业务知识，熟悉学校各项安全管理制度。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">严守岗位职责和工作规程，爱护通讯器材和物防设备。维护值勤岗位区域内的正常秩序、疏导交通。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">及时发现值勤岗位区域的各类安全隐患并上报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">果断处理值勤中发现的问题，发现可疑人、事、物或其他治安信息，应及时盘查或监控，并上报保安班长或相关领导。必要时，启动报警器或向</span><span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">110</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">报警。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">6.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制止师生各种不安全或易造成伤害的行为，发生违章或不服从管理者，应婉言劝阻并妥善处理，不听劝阻的，及时上报保安班长解决。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">7.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">遇有火警或其它紧急情况，应迅速扑救或采取有效措施保护现场，并及时上报保安班长。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">8.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">建立执勤巡逻和交接班记录台账，做好每日值勤问题记录和交接班记录，要求清楚、准确、属实。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:30pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">9.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">按时参加保安人员工作例会和相关业务培训，认真听讲并做好记录。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">10.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">工作中遇到疑难问题，应及时上报保安班长，不得擅作主张或隐瞒不报。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">11.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">在不妨碍保安日常工作的前提下，协助其它部门做好服务工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">12.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">按时完成上级交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十七、学生安全员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做好自身安全工作。模范遵守学校各项安全制度，做安全工作带头人。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">及时制止不安全行为。提醒同学不携带危险物品入校，不翻越围墙、栏杆，不在教室、走道和人群集中的地方追逐嬉闹。发现危险行为要及时制止，如不听劝告要及时向老师报告。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">配合老师做好安全宣传教育工作。开展班级安全宣传工作，组织同学参加各类安全教育活动，增强同学安全意识和自我保护能力。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">协助老师维持活动秩序。课间操、运动会、观看演出、外出集体活动等活动期间，协助班主任组织同学有序上下楼、进退场和上下车等。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">排查安全隐患并及时上报。注意发现校内、班内存在的安全隐患，及时上报班主任和学校有关部门。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十八、保洁员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">做卫生清洁时设置地面防滑警示标志，卫生用具放置整齐，预防师生滑倒、绊倒。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">清洁用品（洁厕灵、消毒液等）应放在指定工作室，禁止将此类用品放置在卫生间等师生可接触到的地方。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">清洁灭火器时，检查是否存在人为动用导致器材失效。如有发现应及时报告相关领导或部门。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">保洁工作时应及时检查相关区域内的安全隐患（尤其是卫生间），如有异常，应及时报告相关领导或部门。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>三十九、校车管理员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">建立校车安全管理制度，落实校车安全管理职责。建立乘车学生档案。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">定期对校车驾驶员和随车照管人员开展安全教育，督促落实各项安全管理制度。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">督促校车驾驶员每天出车前认真检查车辆状况，严禁车辆带病上路，制止超载行为，确保行车安全。配合公安交管部门处理校车违法行为。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">认真排查校车安全隐患。建立校车安全隐患排查制度和台账，定期与校车驾驶员一同对校车安全隐患进行排查，发现安全隐患及时整改。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">完成领导小组交办的其它安全工作。</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-indent:0px;text-align:left;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\"><strong>四十、校车随车照管人员</strong></span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">1.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">学生上下车时，在车下引导、指挥，维护上下车秩序；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">2.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">发现驾驶人无校车驾驶资格，饮酒、醉酒后驾驶，或者身体严重不适以及校车超员等明显妨碍行车安全情形的，制止校车开行，并及时报告校车管理员；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">3.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">清点乘车学生人数，帮助、指导学生安全落座、系好安全带，确认车门关闭后示意驾驶人启动校车；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">4.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">制止学生在校车行驶过程中离开座位等危险行为；</span><span style=\"font-family:宋体;color:#333333;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"color:#000000;font-family:宋体;font-size:12px;font-style:normal;font-weight:normal;text-align:left;text-indent:24pt;background-color:#FFFFFF;\" align=\"left\">\r\n	<span style=\"color:black;font-size:14px;font-family:Microsoft YaHei;\">5.</span><span style=\"font-family:Microsoft YaHei;color:black;font-size:14px;\">核实学生下车人数，确认乘车学生已经全部离车后本人方可离车。</span> \r\n</p>\r\n<br />','',1,1,0,'','中小学校岗位安全工作指南','','2017-06-27',NULL,'中小学校岗位安全工作指南',1),(4,'2017-06-27 09:50:42','F','2017-06-27 11:32:32','admin@qq.com','<p>\r\n	<img src=\"/upload/image/20170627/20170627094313_726.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\">各省、自治区、直辖市教育厅（教委）、综治办、高级人民法院、人民检察院、公安厅（局）、民政厅（局）、司法厅（局）、团委、妇联，新疆生产建设兵团教育局、综治办、人民法院、人民检察院、公安局、民政局、司法局、团委、妇联：</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 在党中央、国务院的正确领导下，在各级党委政府及教育、综治、公安、司法等有关部门和共青团、妇联等群团组织的共同努力下，发生在中小学生之间的欺凌和暴力事件得到遏制，预防青少年违法犯罪工作取得明显成效。但是，由于在落实主体责任、健全制度措施、实施教育惩戒、形成工作合力等方面还存在薄弱环节，少数地方学生之间欺凌和暴力问题仍时有发生，损害了学生身心健康，造成了不良社会影响。为全面贯彻党的教育方针，落实立德树人根本任务，切实防治学生欺凌和暴力事件的发生，现提出如下指导意见。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\">一、积极有效预防学生欺凌和暴力</span></strong> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> <strong>1.切实加强中小学生思想道德教育、法治教育和心理健康教育。</strong></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><strong> </strong><span style=\"font-size:14px;\">各地要紧密联系中小学生的思想实际，积极培育和践行社会主义核心价值观。落实《中小学生守则（2015年修订）》，引导全体中小学生从小知礼仪、明是非、守规矩，做到珍爱生命、尊重他人、团结友善、不恃强凌弱，弘扬公序良俗、传承中华美德。落实《中小学法制教育指导纲要》、《青少年法治教育大纲》，开展“法治进校园”全国巡讲活动，让学生知晓基本的法律边界和行为底线，消除未成年人违法犯罪不需要承担任何责任的错误认识，养成遵规守法的良好行为习惯。落实《中小学心理健康教育指导纲要（2012年修订）》，培养学生健全人格和积极心理品质，对有心理困扰或心理问题的学生开展科学有效的心理辅导，提高其心理健康水平。切实加强家庭教育，家长要注重家风建设，加强对孩子的管教，注重孩子思想品德教育和良好行为习惯培养，从源头上预防学生欺凌和暴力行为发生。</span></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> <strong> 2.认真开展预防欺凌和暴力专题教育。各地要在专项整治的基础上，结合典型案例，集中开展预防学生欺凌和暴力专题教育。</strong></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 要强化学生校规校纪教育，通过课堂教学、专题讲座、班团队会、主题活动、编发手册、参观实践等多种形式，提高学生对欺凌和暴力行为严重危害性的认识，增强自我保护意识和能力，自觉遵守校规校纪，做到不实施欺凌和暴力行为。研制学校防治学生欺凌和暴力的指导手册，全面加强教职工特别是班主任专题培训，提高教职工有效防治学生欺凌和暴力的责任意识和能力水平。要通过家访、家长会、家长学校等途径，帮助家长了解防治学生欺凌和暴力知识，增强监护责任意识，提高防治能力。要加强中小学生违法犯罪预防综合基地和人才建设，为开展防治学生欺凌和暴力专题教育提供支持和帮助。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> <strong> 3.严格学校日常安全管理。</strong></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 中小学校要制定防治学生欺凌和暴力工作制度，将其纳入学校安全工作统筹考虑，健全应急处置预案，建立早期预警、事中处理及事后干预等机制。要加强师生联系，密切家校沟通，及时掌握学生思想情绪和同学关系状况，特别要关注学生有无学习成绩突然下滑、精神恍惚、情绪反常、无故旷课等异常表现及产生的原因，对可能的欺凌和暴力行为做到早发现、早预防、早控制。严格落实值班、巡查制度，禁止学生携带管制刀具等危险物品进入学校，针对重点学生、重点区域、重点时段开展防治工作。对发现的欺凌和暴力事件线索和苗头要认真核实、准确研判，对早期发现的轻微欺凌事件，实施必要的教育、惩戒。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><strong><span style=\"font-size:14px;\"> 4.强化学校周边综合治理。</span></strong><br />\r\n</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 各级综治组织要加大新形势下群防群治工作力度，实现人防物防技防在基层综治中心的深度融合，动员社会各方面力量做好校园周边地区安全防范工作。要依托全国社会治安综合治理信息系统，整合各有关部门信息资源，发挥青少年犯罪信息数据库作用，加强对重点青少年群体的动态研判。进一步加强校园及周边地区社会治安防控体系建设，作为公共安全视频监控建设联网应用示范工作的重要内容，推进校园及周边地区公共安全视频监控系统全覆盖，加大视频图像集成应用力度，实现对青少年违法犯罪活动的预测预警、实时监控、轨迹追踪及动态管控。把学校周边作为社会治安重点地区排查整治工作的重点，加强组织部署和检查考核。要对中小学生欺凌和暴力问题突出的地区和单位，根据《中共中央办公厅 \r\n国务院办公厅关于印发&lt;健全落实社会治安综合治理领导责任制规定&gt;的通知》要求，通过通报、约谈、挂牌督办、实施一票否决权制等方式进行综治领导责任督导和追究。公安机关要在治安情况复杂、问题较多的学校周边设置警务室或治安岗亭，密切与学校的沟通协作，积极配合学校排查发现学生欺凌和暴力隐患苗头，并及时预防处置。要加强学生上下学重要时段、学生途经重点路段的巡逻防控和治安盘查，对发现的苗头性、倾向性欺凌和暴力问题，要采取相应防范措施并通知学校和家长，及时干预，震慑犯罪。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\">二、依法依规处置学生欺凌和暴力事件</span></strong> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"><strong> 5.保护遭受欺凌和暴力学生身心安全。</strong></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><strong></strong><span style=\"font-size:14px;\"> 各地要建立中小学生欺凌和暴力事件及时报告制度，一旦发现学生遭受欺凌和暴力，学校和家长要及时相互通知，对严重的欺凌和暴力事件，要向上级教育主管部门报告，并迅速联络公安机关介入处置。报告时相关人员有义务保护未成年人合法权益，学校、家长、公安机关及媒体应保护遭受欺凌和暴力学生以及知情学生的身心安全，严格保护学生隐私，防止泄露有关学生个人及其家庭的信息。特别要防止网络传播等因素导致事态蔓延，造成恶劣社会影响，使受害学生再次受到伤害。</span></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><strong><span style=\"font-size:14px;\"> 6.强化教育惩戒威慑作用。</span></strong><br />\r\n</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 对实施欺凌和暴力的中小学生必须依法依规采取适当的矫治措施予以教育惩戒，既做到真情关爱、真诚帮助，力促学生内心感化、行为转化，又充分发挥教育惩戒措施的威慑作用。对实施欺凌和暴力的学生，学校和家长要进行严肃的批评教育和警示谈话，情节较重的，公安机关应参与警示教育。对屡教不改、多次实施欺凌和暴力的学生，应登记在案并将其表现记入学生综合素质评价，必要时转入专门学校就读。对构成违法犯罪的学生，根据《刑法》、《治安管理处罚法》、《预防未成年人犯罪法》等法律法规予以处置，区别不同情况，责令家长或者监护人严加管教，必要时可由政府收容教养，或者给予相应的行政、刑事处罚，特别是对犯罪性质和情节恶劣、手段残忍、后果严重的，必须坚决依法惩处。对校外成年人教唆、胁迫、诱骗、利用在校中小学生违法犯罪行为，必须依法从重惩处，有效遏制学生欺凌和暴力等案事件发生。各级公安、检察、审判机关要依法办理学生欺凌和暴力犯罪案件，做好相关侦查、审查逮捕、审查起诉、诉讼监督、审判和犯罪预防工作。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"><strong> 7.实施科学有效的追踪辅导。</strong></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><strong> </strong><span style=\"font-size:14px;\">欺凌和暴力事件妥善处置后，学校要持续对当事学生追踪观察和辅导教育。对实施欺凌和暴力的学生，要充分了解其行为动机和深层原因，有针对性地进行教育引导和帮扶，给予其改过机会，避免歧视性对待。对遭受欺凌和暴力的学生及其家人提供帮助，及时开展相应的心理辅导和家庭支持，帮助他们尽快走出心理阴影，树立自信，恢复正常学习生活。对确实难以回归本校本班学习的当事学生，教育部门和学校要妥善做好班级调整和转学工作。要认真做好学生欺凌和暴力典型事件通报工作，既要充分发挥警示教育作用，又要注意不过分渲染事件细节。</span></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\">三、切实形成防治学生欺凌和暴力的工作合力</span></strong> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"><strong> 8.加强部门统筹协调。</strong></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><strong> </strong><span style=\"font-size:14px;\">各地要把防治学生欺凌和暴力工作作为全面依法治国，建设社会主义和谐社会的重要任务。教育、综治、人民法院、人民检察院、公安、民政、司法、共青团、妇联等部门组织，应成立防治学生欺凌和暴力工作领导小组，明确任务分工，强化工作职责，完善防治办法，加强考核检查，健全工作机制，形成政府统一领导、相关部门齐抓共管、学校家庭社会三位一体的工作合力。</span></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"><strong> 9.依法落实家长监护责任。</strong></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><strong> </strong><span style=\"font-size:14px;\">管教孩子是家长的法定监护职责。引导广大家长要增强法治意识，掌握科学的家庭教育理念，尽量多安排时间与孩子相处交流，及时了解孩子的日常表现和思想状况，积极与学校沟通情况，自觉发挥榜样作用，切实加强对孩子的管教，特别要做好孩子离校后的监管看护教育工作，避免放任不管、缺教少护、教而不当。要落实监护人责任追究制度，根据《民法》等相关法律法规，未成年学生对他人的人身和财产造成损害的，依法追究其监护人的法律责任。</span></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><strong><span style=\"font-size:14px;\"> 10.加强平安文明校园建设。</span></strong><br />\r\n</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 中小学校要把防治学生欺凌和暴力作为加强平安文明校园建设的重要内容。学校党组织要充分发挥政治核心作用，加强组织协调和教育引导。校长是学校防治学生欺凌和暴力的第一责任人，分管法治教育副校长和班主任是直接责任人，要充分调动全体教职工的积极性，明确相关岗位职责，将学校防治学生欺凌和暴力的各项工作落实到每个管理环节、每位教职工。要努力创造温馨和谐、积极向上的校园环境，重视校园绿化、美化和人文环境建设。加强优良校风、教风、学风建设，开展内容健康、格调高雅、丰富多彩的校园活动，形成团结向上、互助友爱、文明和谐的校园氛围，激励学生爱学校、爱老师、爱同学，提高校园整体文明程度。要健全各项管理制度、校规校纪，落实《义务教育学校管理标准》，提高学校治理水平，推进依法依规治校，建设无欺凌和暴力的平安文明校园。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"><strong> 11.全社会共同保护未成年学生健康成长。</strong></span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"justify\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 要建立学校、家庭、社区（村）、公安、司法、媒体等各方面沟通协作机制，畅通信息共享渠道，进一步加强对学生保护工作的正面宣传引导，防止媒体过度渲染报道事件细节，避免学生欺凌和暴力通过网络新媒体扩散演变为网络欺凌，消除暴力文化通过不良出版物、影视节目、网络游戏侵蚀、影响学生的心理和行为，引发连锁性事件。要依托各地12355青少年服务台，开设自护教育热线，组织专业社会工作者、公益律师、志愿者开展有针对性的自护教育、心理辅导和法律咨询。坚持标本兼治、常态长效，净化社会环境，强化学校周边综合治理，切实为保护未成年人平安健康成长提供良好社会环境。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"right\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\">教育部 中央综治办 最高人民法院</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"right\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\">最高人民检察院 公安部 民政部</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"right\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\">司法部 共青团中央 全国妇联</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"right\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\">2016年11月1日</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>','',0,1,0,'','','','2017-06-27',NULL,'教育部等九部门关于防治中小学生欺凌和暴力的指导意见',1),(5,'2017-06-27 10:07:04','F','2017-06-27 11:32:58','admin@qq.com','<div id=\"xxgk_content_redheadbg\" align=\"right\">\r\n	<p id=\"xxgk_content_fwzh_bottom\">\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\">国教督办〔2016〕4号</span> \r\n	</p>\r\n</div>\r\n<div class=\"Custom_UnionStyle\">\r\n	<p>\r\n		<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\">各省、自治区、直辖市人民政府办公厅：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 为落实党中央、国务院关于保障学校安全的总体要求，推动建立科学化、规范化、制度化的中小学（幼儿园）安全保障体系和运行机制，提高安全风险防控能力，特制定《中小学（幼儿园）安全工作专项督导暂行办法》。现印发给你们，请结合实际认真贯彻执行。</span> \r\n	</p>\r\n	<p align=\"right\">\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\">国务院教育督导委员会办公室</span> \r\n	</p>\r\n	<p align=\"right\">\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\">2016年11月30日</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:16px;font-family:Microsoft YaHei;\"><br />\r\n</span></strong> \r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:16px;font-family:Microsoft YaHei;\"><br />\r\n</span></strong> \r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\">中小学（幼儿园）安全工作专项督导暂行办法 </span></strong> \r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第一章 总 则</span></strong> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第一条 \r\n为贯彻落实党中央、国务院关于切实加强学校安全工作的总体要求，督促各地认真做好中小学（幼儿园）（以下简称学校）安全管理工作，根据《教育督导条例》及国家相关政策法规，制定本办法。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第二条 \r\n学校安全工作专项督导是促进地方政府及相关职能部门、学校建立科学化、规范化、制度化的安全保障体系和运行机制，提高学校安全风险防控能力的重要举措。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第三条 \r\n国务院教育督导委员会办公室负责对省级学校安全工作进行专项督导，省、市、县级人民政府教育督导机构负责对下一级及辖区内的学校安全工作进行专项督导。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第四条 实施学校安全工作专项督导应坚持以下原则：</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （一） 统一领导。切实加强组织领导和统筹协调，把学校安全工作作为公共安全和社会治安综合治理的重要内容，定期开展督导检查。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二） 注重实效。完善学校安全工作专项督导形式、内容和方法，因地制宜，确保学校安全工作专项督导取得实效。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （三） 公开透明。坚持标准与方法公开、组织与人员公开、过程与结果公开，主动接受社会监督。</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\">第二章 督导内容</span></strong> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第五条 组织管理</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （一）省级人民政府建立健全学校安全工作组织管理体系，督促市、县级政府落实学校安全工作管理与监督责任情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二）相关职能部门落实学校安全工作资金与资源，开展安全管理培训与指导，监督学校建立健全安全管理机构、落实岗位安全职责，配备安全保卫人员情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第六条 制度建设</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （一）省级人民政府贯彻落实国家有关学校安全工作的法律法规、规章制度和标准规范，建立健全学校安全工作治理机制，制定完善本地方学校安全标准体系，开展学校安全事项认证情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二）相关职能部门各司其职、齐抓共管，完善落实学校安全工作监督、管理，加强学校及周边安全综合治理，建立学生安全区域情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （三）学校建立健全安全管理制度和安全应急机制，按照《中小学幼儿园安全防范工作规范（试行）》要求，落实人防、物防、技防“三防”建设和安全管理各环节、岗位职责情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第七条 预警防范</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （一）相关职能部门建立隐患排查与整治的学校安全预警机制，及时发布安全预警公告情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二）相关职能部门制定学校安全风险清单，开展学校安全检查与动态监测，及时分析和评估安全风险，提出预警信息情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （三）学校建立健全及落实安全教育、日常管理、体育运动、校外活动、公共安全事件、校车安全、食品安全、卫生防疫、自然灾害风险评估和预防情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第八条 教育演练</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （一）教育部门按照《中小学公共安全教育指导纲要》指导学校加强安全教育，落实安全教育进课堂，保障安全教育所需资金、教学资源和师资情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二）相关职能部门指导和参与学校安全教育，开展安全防范进校园活动情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （三）学校按照《中小学幼儿园应急疏散演练指南》开展安全教育，定期组织地震、火灾等应急疏散演练情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第九条 重点治理</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （一）相关职能部门加强溺水、事故、学生欺凌和暴力行为等重点问题预防与应对，及时做好专项报告和统计分析，指导学校履行教育和管理职责情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二）教育部门会同公安等部门及时打击涉及学校、学生安全的违法犯罪行为，维护正常教育教学秩序，建设平安校园情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （三）教育部门及学校健全未成年学生权利保护制度，防范、调查、处理侵害未成年学生身心健康事件，开展心理、行为咨询和矫治活动情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十条 事故处理</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （一）省级人民政府建立健全学校安全事故应对、处理与责任追究机制情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二）相关职能部门及时组织实施救援，落实事故调查、责任认定和善后处理，追究事故相关单位及责任人行政、刑事责任情况。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （三）教育部门及学校妥善处理安全事故纠纷，维护学校正常教育教学秩序情况。</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第三章 组织实施</span></strong> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十一条 日常监督。充分发挥责任督学作用，强化日常检查，促进学校安全工作有序进行。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十二条 \r\n地方自查。省级人民政府及相关职能部门根据指标体系进行自查，并将自查报告在当地政府及省级教育行政部门网站上公示，公示期满后报送国务院教育督导委员会办公室。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十三条 \r\n实地督导。国务院教育督导委员会办公室根据日常监督与地方自查情况，编制实地督导实施细则，随机抽取督学和专家组成督导组，随机确定督导对象，采取听取汇报、查阅材料、重点检查、随机抽查、个别访谈等方式开展实地督导。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十四条 发布报告。国务院教育督导委员会办公室根据各省（区、市）自评和实地督导结果，形成专项督导意见和督导报告，督导报告向社会发布。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十五条 \r\n整改落实。被督导省（区、市）在接到督导组督导意见后，按照整改要求和建议积极进行整改，并在3个月内向国务院教育督导委员会办公室书面报告整改情况。需要立即整改的重大安全隐患，则要在1个月内向国务院教育督导委员会办公室报告整改情况。</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\">第四章 结果运用</span></strong> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十六条 \r\n国务院教育督导委员会办公室建立工作问责机制，把专项督导结果作为评价政府教育工作成效的重要内容，对职责落实不到位的地区给予通报批评，对学校安全工作不力或出现严重问题的地区进行问责。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十七条 \r\n对学校安全工作中出现的特大、重大学校安全责任事故，严重违法、违纪、违规问题，按照法律法规和有关规定开展调查处理。对违纪问题线索，交由纪检监察机关进行调查，严肃追究相关单位和责任人的责任。涉嫌犯罪的，移送司法机关依法处理。</span> \r\n	</p>\r\n	<p align=\"center\">\r\n		<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\">第五章 附 则</span></strong> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十八条 省级人民政府依据本办法制定本省份学校安全工作专项督导实施方案。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 第十九条 本办法自发布之日起施行。</span> \r\n	</p>\r\n</div>','',0,1,0,'','','','2017-06-27',NULL,'国务院教育督导委员会办公室关于印发《中小学（幼儿园）安全工作专项督导暂行办法》的通知',1),(6,'2017-06-27 10:10:32','F','2017-06-27 11:33:15','admin@qq.com','<p>\r\n	<img src=\"/upload/image/20170627/20170627100835_979.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-family:微软雅黑;font-size:14px;color:#4C4C4C;text-indent:2em;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;font-family:Microsoft YaHei;color:#000000;\"><strong><span style=\"font-size:14px;\"> 教育部长陈宝生强调：</span></strong><br />\r\n</span> \r\n</p>\r\n<p style=\"font-family:微软雅黑;font-size:14px;color:#4C4C4C;text-indent:2em;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;color:#000000;\">教育系统要站在讲政治、讲党性的高度, 以更大的努力、更有效的措施、更完善的制度，进一步筑牢校园安全防线, 切实保障广大师生生命财产安全。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"font-family:微软雅黑;font-size:14px;color:#4C4C4C;text-indent:2em;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<span style=\"color:#000000;font-size:14px;font-family:Microsoft YaHei;\"><strong>一是坚持一个提高，做好学校安全工作。</strong></span><span style=\"font-size:14px;font-family:Microsoft YaHei;color:#000000;\">要提高运用法治思维和法治方式研究解决影响校园安全突出问题的能力，建立多方参与、协同配合的学校安全工作机制，强化法律在维护师生权益、化解涉校涉生矛盾纠纷中的权威性，加强高校安全稳定综合防控体系建设和对校园反恐防范工作等方面的指导。</span> \r\n</p>\r\n<p style=\"font-family:微软雅黑;font-size:14px;color:#4C4C4C;text-indent:2em;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<span style=\"color:#000000;font-size:14px;font-family:Microsoft YaHei;\"><strong>二是完善两个机制，优化学校安全环境。</strong></span><span style=\"font-size:14px;font-family:Microsoft YaHei;color:#000000;\">要完善校园安全风险管控机制，全面落实安全管理责任, 形成职责明确、风险可控的管理格局，安全责任全员化、全覆盖。要完善校园周边综合治理机制，建立学生安全区域制度，进一步健全警校合作机制，严厉打击涉及学校和学生安全的违法犯罪行为，为学校正常教育教学创造良好的外部环境。</span> \r\n</p>\r\n<p style=\"font-family:微软雅黑;font-size:14px;color:#4C4C4C;text-indent:2em;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<span style=\"color:#000000;font-size:14px;font-family:Microsoft YaHei;\"><strong>三是突出三项治理，有效降低安全事故发生。</strong></span><span style=\"font-size:14px;font-family:Microsoft YaHei;color:#000000;\">要进行防溺水事故专项治理，及时消除隐患，加强预防教育，确保学生溺水事故得到有效控制。要进行交通安全专项治理，指导中小学校制订详细的校车安全防范预案，加大对非法接送学生车辆的整治力度。要进行学生欺凌与暴力专项治理，系统治理、综合施策，依法依规处置学生欺凌和暴力事件，有效遏制此类事件发生。</span> \r\n</p>\r\n<p style=\"font-family:微软雅黑;font-size:14px;color:#4C4C4C;text-indent:2em;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<span style=\"color:#000000;font-size:14px;font-family:Microsoft YaHei;\"><strong>四是抓好“防、查、教、督”四项常规，做好日常管理工作。</strong></span><span style=\"font-size:14px;font-family:Microsoft YaHei;color:#000000;\">要抓能力建设，坚持人防、物防、技防并重。要抓隐患排查，妥善做好安全防范工作。要抓安全教育，做好把国家安全教育纳入国民教育体系工作安排。要抓督导检查，切实了解真实情况，发现薄弱环节，解决实际问题。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>','',0,1,0,'','','','2017-06-27',NULL,'教育部长陈宝生：做好学校安全工作的“一二三四”',1),(7,'2017-06-27 10:13:22','F','2017-06-27 15:20:47','admin@qq.com','<p style=\"font-size:16px;font-family:\'\\\\5B8B体\', Arial, sans-serif !important;color:#1B1B1B;font-style:normal;font-weight:normal;text-align:start;text-indent:0px;\">\r\n	<span style=\"font-size:14px;\"></span> \r\n</p>\r\n<blockquote>\r\n	<p>\r\n		<span style=\"font-size:14px;\"> 国务院总理李克强4月12日主持召开国务院常务会议，部署推进医疗联合体建设，以深化体制机制改革为群众提供优质便利医疗服务； 部署加强中小学幼儿园安全风险防控体系建设，打造平安校园；通过《中华人民共和国统计法实施条例（草案）》。</span> \r\n	</p>\r\n</blockquote>\r\n<blockquote>\r\n	<p>\r\n		<span style=\"font-size:14px;\"> 会议指出，要把保障中小学和幼儿园安全放在公共安全的突出位置，这事关广大学生健康成长和亿万家庭幸福。 </span> \r\n	</p>\r\n</blockquote>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/upload/image/20170627/20170627101208_788.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>','',15,1,0,'','','','2017-06-27',NULL,'李克强：加强中小学幼儿园安全风险防控体系建设，打造平安校园',1),(8,'2017-06-27 10:15:45','F','2017-06-27 15:16:48','admin@qq.com','<div id=\"xxgk_content_redheadbg\" align=\"right\">\r\n	<p id=\"xxgk_content_fwzh_bottom\">\r\n		<span style=\"font-family:Microsoft YaHei;font-size:14px;\">国教督办〔2017〕5号</span> \r\n	</p>\r\n</div>\r\n<p class=\"Custom_UnionStyle\">\r\n	<span style=\"font-family:Microsoft YaHei;font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">各省、自治区、直辖市教育厅（教委），新疆生产建设兵团教育局：</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 近期，广西、河南、四川、山东等地中小学、幼儿园（以下简称学校）发生安全事故，造成学生伤亡。这些安全事故的发生，反映出一些地方和学校存在安全意识不强、制度落实不到位等问题。为切实加强学校安全管理工作，保障学生安全，现紧急通知如下：</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 一、切实提高认识。</span></strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\">各地教育行政部门和学校要以对党和人民高度负责的态度，充分认识做好学校安全工作的重要性、紧迫性，牢固树立安全工作红线意识和底线思维，坚持以人为本、安全第一、预防为主、综合治理，不断研究学校安全工作中出现的新情况、新问题，加强工作的前瞻性、预见性，狠抓落实，切实保障广大师生生命财产安全，为党的十九大胜利召开营造良好氛围和环境。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 二、完善工作机制。</span></strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\">各地教育行政部门和学校要进一步完善校园安全风险管控机制，按照学校岗位安全工作职责要求，严格落实各项管理制度和措施，将安全管理融入到学校日常工作全过程。要完善校园周边治安综合治理机制，加强与公安、综治、宣传等有关部门在信息沟通、专项整治、应急处置、舆情应对等方面的协调配合，形成常态化的安全监管工作机制。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 三、开展隐患排查。</span></strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\">各地教育行政部门和学校要以防溺水、校舍、消防、食品、上下学交通、学生欺凌与暴力等为重点，结合本地实际，立即组织开展一次安全隐患大排查。对排查出的问题，要按照“谁主管、谁负责”的原则，建立台账、落实责任、细化措施，明确时限，确保全面整改落实。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 四、严格信息报送。</span></strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\">各地教育行政部门和学校要认真落实教育系统突发公共事件应急处置工作有关要求，严格执行信息报送制度，遇有突发事件和重要紧急情况必须第一时间上报教育部总值班室。对不第一时间报送或瞒报、漏报安全事故信息的，要严格按照相关规定严肃追究当事人和相关领导的责任。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 五、强化应急管理。</span></strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\">各地教育行政部门和学校要建立健全学校安全事件应急管理机制，完善、细化各项应急处置预案，落实人员职责，做好应急工作准备，确保一旦发生突发事件，能够快速反应、高效应对、迅速管控。要严格按照“属地管理”和“分级管理”的原则，根据突发事件分级标准，科学决策、精心组织、统一指挥、妥善处置，最大限度降低学校安全事故危害。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 六、做好舆情应对。</span></strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\">各地教育行政部门要在当地党委、政府的领导下，加强与宣传、公安等部门沟通协作，建立健全教育舆情实时监测机制，及时跟踪、分析教育舆情，发布预警信息，努力掌握教育舆情的主动权。要加强与媒体的沟通，第一时间发布准确信息，引导舆论理性表达，适时公布事故处置情况，及时回应社会关切，正确引导社会舆论。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 七、加强安全教育。</span></strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\">各地教育行政部门和学校要根据学生身心特点和认知能力，把安全教育贯穿于学校教育各个环节，灵活采取多种形式和载体，着力提高针对性与实效性。要定期开展各类专题教育，经常性地开展应急疏散演练，不断提高学生的安全防范意识、自救自护技能和应急处置能力。要积极做好学生家长的安全教育宣传工作，严防脱离学校监管期间发生安全事故。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\">\r\n	<strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\"> 八、加强督导检查。</span></strong><span style=\"font-family:Microsoft YaHei;font-size:14px;\">各地教育督导部门要按照《中小学（幼儿园）安全工作专项督导暂行办法》要求，将学校安全工作列为教育督导的重要内容，加强对地方政府及各有关部门、学校落实安全工作职责的督导检查，将督导结果作为评价政府教育工作和学校管理工作成效的重要内容。</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"right\">\r\n	<span style=\"font-family:Microsoft YaHei;font-size:16px;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"right\">\r\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">国务院教育督导委员会办公室</span> \r\n</p>\r\n<p class=\"Custom_UnionStyle\" align=\"right\">\r\n	<span style=\"font-family:Microsoft YaHei;font-size:14px;\">2017年4月19日</span> \r\n</p>\r\n<br />','',5,1,0,'','','','2017-06-27',NULL,'国务院教育督导委员会办公室关于加强中小学（幼儿园）安全工作的紧急通知',1),(9,'2017-06-27 10:20:46','F','2017-06-27 15:16:32','admin@qq.com','<p id=\"content_fwzh\" align=\"right\">\r\n	<span style=\"font-size:14px;font-family:Microsoft YaHei;\">国办发〔2017〕35号</span> \r\n</p>\r\n<div id=\"content_body_txt\">\r\n	<div class=\"TRS_Editor\">\r\n		<p>\r\n			<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><br />\r\n</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\">各省、自治区、直辖市人民政府，国务院各部委、各直属机构：</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 校园应当是最阳光、最安全的地方。加强中小学、幼儿园（以下统称学校）安全工作是全面贯彻党的教育方针，保障学生健康成长、全面发展的前提和基础，关系广大师生的人身安全，事关亿万家庭幸福和社会和谐稳定。长期以来，党中央、国务院和地方各级党委、政府高度重视学校安全工作，采取了一系列措施维护学校及周边安全，学校安全形势总体稳定。但是，受各种因素影响，学校安全工作还存在相关制度不完善、不配套，预防风险、处理事故的机制不健全、意识和能力不强等问题。为进一步加强和改进学校安全工作，经国务院同意，现就建立健全学校安全风险防控体系提出以下意见：</span> \r\n		</p>\r\n		<p>\r\n			<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 一、总体要求 </span></strong> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （一）指导思想。高举中国特色社会主义伟大旗帜，全面贯彻党的十八大和十八届三中、四中、五中、六中全会精神，深入贯彻习近平总书记系列重要讲话精神和治国理政新理念新思想新战略，认真落实党中央、国务院决策部署，运用法治思维和法治方式推进综合改革、破解关键问题，建立科学系统、切实有效的学校安全风险防控体系，营造良好教育环境和社会环境，为学生健康成长、全面发展提供保障。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二）基本原则。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 坚持统筹协调、综合施策。将学校安全作为公共安全和社会治安综合治理的重要内容，加强组织领导和协调配合，充分发挥政府、学校、家庭、社会各方面作用，运用法律、行政、社会服务、市场机制等各种方式，综合施策、形成合力。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 坚持以人为本、全面防控。将可能对学生身心健康和生命安全造成影响的各种不安全因素和风险隐患全面纳入防控范畴，科学预防、系统应对、不留死角。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 坚持依法治理、立足长效。突出制度建设的根本性和重要性，依据法治原则和法律规定，做好顶层设计，依法明确各方主体权利、义务与职责，形成防控学校安全风险的长效机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 坚持分类应对、突出重点。坚持问题导向，根据不同区域、地方以及不同层次类型学校的实际，区分风险的类型和特点，有针对性地构建安全风险防控机制，集中解决群众关心、社会关注的校园安全问题。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （三）工作目标。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 针对影响学校安全的突出问题、难点问题，进一步整合各方面力量，加强和完善相关制度、机制，深入改革创新，加快形成党委领导、政府负责、社会协同、公众参与、法治保障，科学系统、全面规范、职责明确的学校安全风险预防、管控与处置体系，切实维护师生人身安全，保障校园平安有序，促进社会和谐稳定。</span> \r\n		</p>\r\n		<p>\r\n			<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 二、完善学校安全风险预防体系</span></strong> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （四）健全学校安全教育机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 将提高学生安全意识和自我防护能力作为素质教育的重要内容，着力提高学校安全教育的针对性与实效性。将安全教育与法治教育有机融合，全面纳入国民教育体系，把尊重生命、保障权利、尊重差异的意识和基本安全常识从小根植在学生心中。在教育中要适当增加反欺凌、反暴力、反恐怖行为、防范针对未成年人的犯罪行为等内容，引导学生明确法律底线、强化规则意识。学校要根据学生群体和年龄特点，有针对性地开展安全专题教育，定期组织应对地震、火灾等情况的应急疏散演练。教育部门要将安全知识作为校长、教师培训的必要内容，加大培训力度并组织必要的考核。各相关部门和单位要组织专门力量，积极参与学校安全教育，广泛开展“安全防范进校园”等活动。鼓励各种社会组织为学校开展安全教育提供支持，设立安全教育实践场所，着力普及和提升家庭、社区的安全教育。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （五）完善有关学校安全的国家标准体系和认证制度。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 不断健全学校安全的人防、物防和技防标准并予以推广。根据学校特点，以保护学生健康安全为优先原则，加强重点领域标准的制修订工作，尽快制定一批强制性国家标准，逐步形成有关学校安全的国家标准体系。建立学校安全事项专项认证及采信推广机制，对学校使用的关系学生安全的设施设备、教学仪器、建筑材料、体育器械等，按照国家强制性产品认证和自愿性产品认证规定，做好相关认证工作，严格控制产品质量。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （六）探索建立学生安全区域制度。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 加强校园周边综合治理，在学校周边探索实行学生安全区域制度。在此区域内，依法分别作出禁止新建对环境造成污染的企业、设施，禁止设立上网服务、娱乐、彩票专营等营业场所，禁止设立存在安全隐患的场所等相应要求。在学生安全区域内，公安机关要健全日常巡逻防控制度，加强学校周边“护学岗”建设，完善高峰勤务机制，优先布设视频监控系统，增强学生的安全感；公安交管部门要加强交通秩序管理，完善交通管理设施。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （七）健全学校安全预警和风险评估制度。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 教育部门要会同相关部门制定区域性学校安全风险清单，建立动态监测和数据搜集、分析机制，及时为学校提供安全风险提示，指导学校健全风险评估和预防制度。要建立台账制度，定期汇总、分析学校及周边存在的安全风险隐患，确定整改措施和时限；在出现可能影响学校安全的公共安全事件、自然灾害等风险时，要第一时间通报学校，指导学校予以防范。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （八）探索建立学校安全风险防控专业服务机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 积极培育可以为学校提供安全风险防控服务的专业化社会组织。采取政府购买服务等方式，鼓励、引导和支持具备相应专业能力的机构、组织，研发、提供学校安全风险预防、安全教育相关的服务或者产品，协助教育部门制定、审核学校安全风险防控预案和相关标准，组织、指导学校有针对性地开展专项安全演练、预防和转移安全风险等工作。</span> \r\n		</p>\r\n		<p>\r\n			<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 三、健全学校安全风险管控机制</span></strong> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （九）落实安全管理主体责任。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 教育部门、公安机关要指导、监督学校依法健全各项安全管理制度和安全应急机制。学校要明确安全是办学的底线，切实承担起校内安全管理的主体责任，对校园安全实行校长（园长）负责制，健全校内安全工作领导机构，落实学校、教师对学生的教育和管理责任，狠抓校风校纪，加强校内日常安全管理，做到职责明确、管理有方。在风险可控的前提下，学校应当积极组织体育锻炼、户外活动等，培养学生强健的体魄。学生在校期间，对校园实行封闭化管理，并根据条件在校门口设置硬质防冲撞设施，阻止人员、车辆等非法进入校园。各类中小学校外活动场所、以学生为主要对象的各类培训机构和课外班等，由地方政府统筹协调有关部门承担安全监管责任，督促举办者落实安全管理责任。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十）建立专兼职结合的学校安保队伍。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 学校应当按照相关规定，根据实际和需要，配备必要的安全保卫力量。除学生人数较少的学校外，每所学校应当至少有1名专职安全保卫人员或者受过专门培训的安全管理人员。地方人民政府、有条件的学校可以以购买服务等方式，将校园安全保卫服务交由专门保安服务公司提供。学校要与社区、家长合作，有条件的建立学校安全保卫志愿者队伍，在上下学时段维护学校及校门口秩序。寄宿制学校要根据需要配备宿舍管理人员。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十一）着力建设安全校园环境。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 各地要坚持安全优先、勤俭节约的原则开展校园建设。学校建设规划、选址要严格执行国家相关标准规范，对地质灾害、自然灾害、环境污染等因素进行全面评估。各地要建立健全校舍安全保障长效机制，保证学校的校舍、场地、教学及生活设施等符合安全质量和标准。校舍建设要严格执行国家建筑抗震有关技术规范和标准，有条件建设学校体育馆的地方，要按照国家防灾避难相关标准建设。完善学校安全技术防范系统，在校园主要区域要安装视频图像采集装置，有条件的要安装周界报警装置和一键报警系统，做到公共区域无死角。建立校园工程质量终身责任制，凡是在校园工程建设中出现质量问题导致严重后果的建设、勘察、设计、施工、监理单位，一旦查实，承担终身责任并限制进入相关领域。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十二）进一步健全警校合作机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 各级教育部门、公安机关和学校要在信息沟通、应急处置等方面加强协作，健全联动机制。公安机关要进一步完善与维护校园安全相适应的组织机构设置形式和警力配置，加强学校及周边警务室建设，派出经验丰富的民警加强学校安全防范工作指导。要将校园视频监控系统、紧急报警装置接入公安机关、教育部门的监控或报警平台，并与公共安全视频监控联网共享平台对接，逐步建立校园安全网上巡查系统，及时掌握、快速处理学校安全相关问题。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十三）健全相关部门日常管理职责体系。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 政府各相关部门要切实承担起学校安全日常管理的职责。卫生计生部门要加强对学校卫生防疫和卫生保健工作的监督指导，对于学校出现的疫情或者学生群体性健康问题，要及时指导教育部门或者学校采取措施。食品药品监管部门对学校食堂和学校采购的用于学生集体使用的食品、药品要加强监督检查，指导、监督学校落实责任，保障食品、药品符合相关标准和规范。住房城乡建设部门要加强对学校工程建设过程的监管。环保部门要加强对学校及周边大气、土壤、水体环境安全的监管。交通运输部门要加强对提供学生集体用车服务的道路运输企业的监管，综合考虑学生出行需求，合理规划城市公共交通和农村客运线路，为学生和家长选择公共交通出行提供安全、便捷的交通服务。质量监督部门应当对学校特种设备实施重点监督检查，配合教育部门加强对学校采购产品的质量监管，在学校建立产品质量安全风险信息监测采集机制。公安消防部门要依法加强对学校的消防安全检查，指导学校落实消防安全责任，消除火灾隐患。综治、工商、文化、新闻出版广电、城市管理等部门要落实职责，加强对校园周边特别是学生安全区域内有关经营服务场所、经营活动的管理和监督，消除安全隐患。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十四）构建防控学生欺凌和暴力行为的有效机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 教育部门要会同有关部门研究制定学生欺凌和暴力行为早期发现、预防以及应对的指导手册，建立专项报告和统计分析机制。学校要切实履行教育、管理责任，设立学生求助电话和联系人，及早发现、及时干预和制止欺凌、暴力行为。对有不良行为、暴力行为的学生，探索建立由校园警务室民警或者担任法治副校长、辅导员的民警实施训诫的制度。对实施暴力情节严重，构成违法犯罪的学生，公安、司法机关要坚持宽容但不纵容、关爱又严管的原则，指定专门机构或者专门人员依法处理，特别是对犯罪性质和情节恶劣、手段残忍、后果严重的，必须坚决依法惩处，形成积极正面的教育作用。改革完善专门教育制度，健全专门学校接收学生进行教育矫治的程序，完善专门学校管理体制和运行机制。网络管理部门发现通过网络传播的欺凌或者校园暴力事件，要及时予以管控并通报相关部门。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十五）严厉打击涉及学校和学生安全的违法犯罪行为。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 对非法侵入学校扰乱教育教学秩序、侵害师生生命财产安全等违法犯罪行为，公安机关要依法坚决处置、严厉打击，实行专案专人制度。进一步深化平安校园创建活动。建立学校周边治安形势研判预警机制，对涉及学校和学生安全的违法犯罪行为和犯罪团伙，要及时组织开展专项打击整治行动，防止发展蔓延。教育部门要健全学校对未成年学生权利的保护制度，对体罚、性骚扰、性侵害等侵害学生人身健康的违法犯罪行为，要建立零容忍制度，及早发现、及时处理、从严问责，应当追究法律责任的，要协同配合公安、司法机关严格依法惩处。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十六）形成广泛参与的学生安全保护网络。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 教育部门要健全对校园内发生的侵害学生人身权利行为的监督机制和举报渠道，建立规范的调查处理程序。有关部门要与学校、未成年人保护组织、家长加强衔接配合，共同构建对受到伤害学生和涉嫌违法犯罪学生的心理疏导、安抚救助和教育矫正机制。共青团组织要完善未成年人维权热线，提供相应法律咨询、心理辅导等。妇联组织要积极指导家长进行正确的家庭教育，开展未成年人家庭保护相关法律法规宣传，组织落实对未成年人家庭保护的法律规定。支持和鼓励律师协会、政法院校等法律专业组织和单位，设立未成年学生保护的公益性组织，利用和发展未成年人保护志愿律师网络，为学生维护合法权益提供法律服务。</span> \r\n		</p>\r\n		<p>\r\n			<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 四、完善学校安全事故处理和风险化解机制</span></strong> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十七）健全学校安全事故应对机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 学校发生重特大安全事故，地方政府要在第一时间启动相应的应急处理预案，统一领导，及时动员和组织救援和事故调查、开展责任认定及善后处理，并及时回应社会关切。发生重大自然灾害、公共安全事故，应当优先组织对受影响学校开展救援。教育部门应当指导学校建立安全事故处置预案，健全学校安全事故的报告、处置和部门协调机制。在校内及校外教育教学活动中发生安全事故，学校应当及时组织教职工参与抢险、救助和防护，保障学生身体健康和人身安全。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十八）健全学校安全事故责任追究和处理制度。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 发生造成师生伤亡的安全事故，有关部门要依法认定事故责任，学校及相关方面有责任的，要严肃追究有关负责人的责任；学校无责任的，要澄清事实、及时说明，避免由学校承担不应承担的责任。司法机关要加强案例指导，引导社会依法合理认识学校的安全责任，明确学生监护人的职责。积极利用行政调解、仲裁、人民调解、保险理赔、法律援助等方式，通过法治途径和方式处理学校安全事故，及时依法赔偿，理性化解纠纷。对围堵校园、殴打侮辱教师、干扰学校正常教育教学秩序等“校闹”行为，公安机关要及时坚决予以制止。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （十九）建立多元化的事故风险分担机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 学校举办者应当按规定为学校购买校方责任险，义务教育阶段学校投保校方责任险所需经费从公用经费中列支，其他学校投保校方责任险的费用，由各省（区、市）按照国家有关规定执行。各地要根据经济社会发展情况，结合实际合理确定校方责任险的投保责任，规范理赔程序和理赔标准。有条件的地方，可以积极探索与学生利益密切相关的食品安全、校外实习、体育运动伤害等领域的责任保险，充分发挥保险在化解学校安全风险方面的功能作用。保险监管部门要加强对涉及学校的保险业务的监督和管理，会同教育部门依法规范保险公司与学校的合作，严禁以学校名义指定学生购买或者向学生直接推销保险产品。要大力增强师生和家长的保险意识，引导家长根据自愿原则参加保险，分担学生在学校期间因意外而发生的风险。鼓励各种社会组织设立学校安全风险基金或者学生救助基金，健全学生意外伤害救助机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二十）积极构建学校依法处理安全事故的支持体系。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 各地要采取措施，在中小学推广建立法律顾问制度。教育部门和学校要建立健全新闻发言人制度，增强事故发生后的舆情应对能力。要发挥好安全风险防控专业服务机制的作用，借助专业机构在损失评估、理赔服务、调处纠纷等方面的力量，帮助学校妥善处理事故。教育、司法行政部门要会同相关部门，探索在有需求的县（市、区）设立学校安全事故人民调解委员会，吸纳具有较强专业知识和社会公信力、知名度，热心调解和教育事业的社会人士担任人民调解员，依法调解学校安全事故民事赔偿纠纷。</span> \r\n		</p>\r\n		<p>\r\n			<strong><span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 五、强化领导责任和保障机制</span></strong> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二十一）加强组织领导。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 各地要高度重视学校安全风险防控工作，将学校安全作为经济社会发展的重要指标和社会治理的重要内容，建立党委领导、政府主导、相关部门和单位参加的学校安全风险防控体系建设协调机制，定期研究和及时解决学校安全工作中的突出问题，切实为学校正常开展教育教学活动和课外实践活动提供支持和保障。各相关部门和单位要制定具体细则或办法，落实本意见提出的工作要求，加强沟通协调，协同推动防控机制建设，形成各司其职、齐抓共管的工作格局。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二十二）强化基础保障。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 各级教育部门、公安机关要明确归口负责学校安全风险防控的专门机构，完善组织体系与工作机制，配齐配强工作力量。各级机构编制部门要根据工作需要，优化现有编制结构，适当向教育部门、公安机关负责学校安全风险防范的机构倾斜。各级财政部门要按规定将学校安全风险防控经费纳入一般公共预算，保障合理支出。要健全学校安全风险防控的网络管理与服务系统，整合各方面力量，积极利用互联网和信息技术，为学校提供便捷、权威的安全风险防控的专业咨询和技术支持服务。加快完善学校安全法律规范，推动适时修改关于未成年人保护的相关法律，启动防控校园暴力行为等相关法律的制修订工作，构建完善的法律保障体系。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> （二十三）健全督导与考核机制。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 各级人民政府教育督导机构要将学校安全工作作为教育督导的重要内容，加强对政府及各有关部门、学校落实安全风险防控职责的监督、检查。对重大安全事故或者产生重大影响的校园安全事件，要组织专项督导并向社会公布督导报告。对学校安全事故频发的地区，要以约谈、挂牌督办等方式督促其限期整改。教育部门要将安全风险防控工作的落实情况，作为考核学校依法办学和学校领导班子工作的重要内容。</span> \r\n		</p>\r\n		<p>\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\"> 高等学校应当结合自身实际，参照本意见，健全安全风险防控体系，完善工作机制和建设方案，所在地的地方人民政府及有关部门应当予以指导、支持，切实履行相关职责。</span> \r\n		</p>\r\n		<p align=\"right\">\r\n			<span style=\"font-size:16px;font-family:Microsoft YaHei;\"><br />\r\n</span> \r\n		</p>\r\n		<p align=\"right\">\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\">国务院办公厅</span> \r\n		</p>\r\n		<p align=\"right\">\r\n			<span style=\"font-size:14px;font-family:Microsoft YaHei;\">2017年4月25日</span> \r\n		</p>\r\n	</div>\r\n</div>','',7,1,0,'','','','2017-06-27',NULL,'国务院办公厅关于加强中小学幼儿园安全风险防控体系建设的意见',1),(10,'2017-06-27 10:26:17','F','2017-06-27 15:21:06','admin@qq.com','<blockquote>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"> 据教育部网站消息，教育部办公厅发布紧急通知，要求加强中小学(幼儿园)周边安全风险防控工作。</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"> 通知要求各地教育部门和学校会同综治、公安、工商、食药监、文化、城市管理等部门，以学校周边刑满释放人员、社区矫正对象、易肇事肇祸精神病人等特殊人群和游商走贩、违规经营摊点为重点，全面开展一次安全隐患大排查。</span> \r\n	</p>\r\n</blockquote>\r\n<blockquote>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"> 通知指出，近期，江苏、山东、广西等地中小学、幼儿园(以下简称学校)周边地区先后发生数起安全事故，严重危害师生生命安全。</span> \r\n	</p>\r\n</blockquote>\r\n<blockquote>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"><strong>一、提高认识，健全防控机制</strong><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"> 各地教育部门要在当地党委、政府领导下，牢固树立安全工作红线意识和底线思维，认真贯彻落实《国务院办公厅关于加强中小学幼儿园安全风险防控体系建设的意见》要求，指导学校落实各项安全管理制度，加大安全教育力度，健全安全应急机制。会同相关部门制定区域性学校安全风险清单，提供安全风险提示，指导学校健全风险评估和预防制度。加强与公安、综治等部门沟通协作，健全联动机制，及时会商和处理涉及学校周边地区安全稳定的突出问题。</span> \r\n	</p>\r\n</blockquote>\r\n<blockquote>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"><strong>二、强化排查，化解安全隐患</strong><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"> 各地教育部门和学校要会同综治、公安、工商、食药监、文化、城市管理等部门，以护校安园、打非治违等专项整治工作为抓手，以学校周边刑满释放人员、社区矫正对象、易肇事肇祸精神病人等特殊人群和游商走贩、违规经营摊点为重点，全面开展一次安全隐患大排查，及时化解涉校涉生矛盾纠纷，消除各类安全隐患，确保学校周边环境安全、稳定、有序。对排查出的问题，要按照“谁主管、谁负责”的原则，建立台账、落实责任、细化措施，明确时限，确保全面整改落实，防患于未然。<strong><br />\r\n</strong></span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"><strong>三、加强督查，落实安全责任</strong><br />\r\n</span> \r\n	</p>\r\n	<p>\r\n		<span style=\"font-size:14px;color:#000000;\"> 各地教育督导部门要严格按照《中小学(幼儿园)安全工作专项督导暂行办法》规定，将学校周边安全风险防控工作列为教育督导的重要内容，加强对地方政府及各有关部门、学校落实安全工作职责的督导检查，将督导结果作为评价政府教育工作和学校管理工作成效的重要内容。对履职不到位、责任不落实、措施不得力、管理不规范、安全问题突出的单位及学校，要采取约谈、通报批评、印发整改通知书等措施，限期整改。造成严重后果的，要依法追究有关责任人责任。 </span> \r\n	</p>\r\n</blockquote>\r\n<p>\r\n	<br />\r\n</p>','',11,1,0,'','','','2017-06-27',NULL,'教育部办公厅发布紧急通知，要求加强中小学(幼儿园)周边安全风险防控工作',1);

